declare namespace BaseInfo {
  export type NavigationBarTextStyle = 'black' | 'white'

  export type NavigationStyle = 'default' | 'custom'

  export type BackgroundTextStyle = 'dark' | 'light'

  export type PageOrientation = 'portrait' | 'auto' | 'landscape'

  // 运行时的 pageOrinetation 是确定的值。不能是 auto
  export type RuntimePageOrientation = Exclude<BaseInfo.PageOrientation, 'auto'>

  export type DeviceOrientation =
    | RuntimePageOrientation
    | 'landscapeLeft'
    | 'landscapeRight'
}