/// <reference path="./base.d.ts" />
/// <reference path="./app.d.ts" />

declare namespace GameJSON {
  export type deviceOrientation = BaseInfo.DeviceOrientation

  export interface subpackage {
    root: string
    name?: string
    independent?: boolean,
    plugins?: {
      [alias: string]: IPlugin
    }
  }

  export interface loadingImageInfo {
    path: string
    progressBarColor: string
  }

  export interface IPlugin {
    version: string
    provider: string
    path?: string
  }
}

interface IGameJSON {
  navigateToMiniProgramAppIdList: string[]
  subpackages: GameJSON.subpackage[]
  subPackages: GameJSON.subpackage[]
  loadingImageInfo: GameJSON.loadingImageInfo
  deviceOrientation: GameJSON.deviceOrientation
  networkTimeout: AppJSON.INetworkTimeoutConfig
  openDataContext: string
  workers: string
  plugins: {
    [alias: string]: GameJSON.IPlugin
  }
}






