declare namespace SiteMapJSON {
  export interface ISiteMapJSON {
    desc: string
    rules: Rule[]
  }

  export interface Rule {
    action: 'allow' | 'disallow'
    page: string
    params?: string[]
    matching?: 'exact' | 'inclusive' | 'exclusive' | 'partial'
    priority?: number
    _index?: number  // 该属性是为了保存规则最初的位置，因为zhi根据优先级进行排序
  }
}
